SELECT * FROM hotels
WHERE country = 'ES' AND stars >= 4 AND rating >= 7 AND review_count > 10
AND (cribs = 1 OR kids_friendly_buffet = 1 OR child_pool = 1 OR playground = 1);

SELECT DISTINCT ON (country) * FROM hotels
WHERE (pool = 1 OR (beach_access IN ('private beach', 'beach access', 'no', 'beach access') AND ocean_view = 1))
ORDER BY RAND() LIMIT 3;

SELECT * FROM hotels
WHERE country = 'TR' AND city = 'Istanbul' AND spa = 1 AND fitness_center = 1 AND ocean_view = 1 AND stars >= 4
ORDER BY stars DESC;
